<?php

namespace coreBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
* CombatsRepository
*
* This class was generated by the Doctrine ORM. Add your own custom
* repository methods below.
*/
class WebsiteStyxuserbaseZonesRepository extends EntityRepository
{
  public function findById(Integer $id)
  {
    $repository = $this->getEntityManager()->getRepository('coreBundle:WebsiteStyxuserbaseZones');

    $query = $repository->createQueryBuilder('c')
      ->where('c.id = $id')

    $result = $query->getQuery()->getResult();
    return $result;
  }

  public function findByStyxuserbase(Integer $user)
  {
    $repository = $this->getEntityManager()->getRepository('coreBundle:WebsiteStyxuserbaseZones');

    $query = $repository->createQueryBuilder('c')
      ->where('c.styxuserbase = $user->getId()')

    $result = $query->getQuery()->getResult();
    return $result;
  }
}
